        <!-- Additional main content -->
    </div>




    <script>
// Function to toggle the sidebar and overlay visibility
function toggleSidebar() {
    var sidebar = document.getElementById("sidebar");
    var overlay = document.getElementById("overlay");




    sidebar.classList.toggle('active');
    overlay.classList.toggle('active');
}




// Event listeners to toggle the sidebar on button and overlay click
document.addEventListener('DOMContentLoaded', function() {
    var menuBtn = document.querySelector('.menu-btn');
    var overlay = document.getElementById('overlay');




    menuBtn.addEventListener('click', toggleSidebar);
    overlay.addEventListener('click', toggleSidebar);
});




// Event listener to ensure the sidebar state is consistent when resizing the window
window.addEventListener('resize', function() {
    var sidebar = document.getElementById("sidebar");
    var overlay = document.getElementById("overlay");




    if (window.innerWidth > 768) {
        sidebar.classList.remove('active');
        overlay.classList.remove('active');
    }
});












// otro dom








// Esta función crea el menú y lo añade al DOM
function createMenu() {
    // Crear el botón de ajustes y sus atributos
    const settingsBtn = document.createElement('button');
    settingsBtn.id = 'settings-btn';
    settingsBtn.setAttribute('aria-expanded', 'false');
    settingsBtn.innerHTML = 'Ajustes <svg>...</svg>'; // Agregar el SVG adecuado




    // Crear el contenedor del menú
    const menuContainer = document.createElement('div');
    menuContainer.id = 'menu';
    menuContainer.classList.add('menu', 'hidden');




    // Crear el botón de cerrar sesión y agregarlo al menú
    const logoutBtn = document.createElement('button');
    logoutBtn.innerHTML = 'Cerrar sesión <svg>...</svg>'; // Agregar el SVG adecuado
    menuContainer.appendChild(logoutBtn);




    // Añadir el botón de ajustes y el menú al cuerpo del documento
    document.body.appendChild(settingsBtn);
    document.body.appendChild(menuContainer);




    // Event listener para el botón de ajustes
    settingsBtn.addEventListener('click', () => {
        const isExpanded = settingsBtn.getAttribute('aria-expanded') === 'true';
        settingsBtn.setAttribute('aria-expanded', String(!isExpanded));
        menuContainer.classList.toggle('hidden');
    });
}




document.addEventListener('DOMContentLoaded', () => {
    createMenu(); // Llamar a la función para crear el menú al cargar el documento
    // Tus otros event listeners aquí
});








    </script>


<script>


    $(document).ready(function() {
    var endpoint = "https://script.google.com/macros/s/AKfycbz0aQOYzTO1wLUjElW6f2arcdJtFmNdU95olmdrjZGgQv4zXn_MkfgOXWiwQl83eGcJ/exec";
   
    $.ajax({
        url: endpoint,
        method: 'POST',
        dataType: 'json',
        success: function(response) {
            var $tbody = $('#ventasTable tbody');
            response.forEach(function(item) {
                var $row = $('<tr>');
                $row.append($('<td>').text(item.fecha));
                $row.append($('<td>').text(item.cliente));
 
                // Concatenar Marca, Modelo, y Descripción en una celda
                var detallesProducto = item.marca + ' ' + item.modelo + ' - ' + item.descripcion;
                $row.append($('<td>').text(detallesProducto));
 
                $row.append($('<td>').text(item.comprobante));
                $row.append($('<td>').text(item.pago));
 
                $row.append($('<td>').text(item.cantidad));
                $row.append($('<td>').text(item.total));
                $row.append($('<td>').text(item.estado));
 
                $tbody.append($row);
            });
        },
        error: function(error) {
            console.error('Error:', error);
        }
    });
  });
 
      </script>
</body>
</html>
